package actionTest;import PostTest.SendRequest;import weibo4j.org.json.JSONObject;public class DeleteUserTest {	/**	 * @param args	 */	public static void main(String[] args) throws Exception{				String CorGroupInfo = TestConfig.getValue("OnLineHost")+",CorpusManger";		String MsgGroupInfo = TestConfig.getValue("OnLineHost")+",MessageCenter";		String url = TestConfig.getValue("OnLineApi")+"/deleteUser";				JSONObject jsonDeleteUser = new JSONObject();		jsonDeleteUser.put("UID", "3305972627");		jsonDeleteUser.put("CorGroupInfo", CorGroupInfo);		jsonDeleteUser.put("MsgGroupInfo", MsgGroupInfo);								String strRet = SendRequest.sendPost(url, "data="+jsonDeleteUser.toString());		System.out.println("DeleteUserTest:"+strRet);	}}