package actionTest;import PostTest.SendRequest;import weibo4j.org.json.JSONObject;public class ReplyTest {	/**	 * @param args	 */	public static void main(String[] args) throws Exception{						String MsgGroupInfo = TestConfig.getValue("TestHost")+",MessageCenter";		String url = TestConfig.getValue("TestApi")+"/replyMessage";				JSONObject jsonReply = new JSONObject();		jsonReply.put("UID", TestConfig.getValue("TestUID"));		jsonReply.put("MID", "");		jsonReply.put("ToID", "1437859313");		jsonReply.put("Type", "0");		jsonReply.put("Content", "aaaaaa");		jsonReply.put("MsgGroupInfo", MsgGroupInfo);				System.out.println(jsonReply.toString(5));				String strRet = SendRequest.sendPost(url, "data="+jsonReply.toString());		System.out.println("ReplyTest:"+strRet);	}}