package actionTest;import weibo4j.org.json.JSONObject;import PostTest.SendRequest;public class SetUserInfoTest {	/**	 * @param args	 */	public static void main(String[] args) throws Exception{				String MsgGroupInfo = TestConfig.getValue("OnLineHost")+",MessageCenter";		String url = TestConfig.getValue("OnLineApi")+"/setUserInfo";						JSONObject jsonSetUserInfo = new JSONObject();		jsonSetUserInfo.put("UID", "3306169867");		jsonSetUserInfo.put("MsgGroupInfo", MsgGroupInfo);				System.out.println("参数:"+jsonSetUserInfo.toString(5));				String strRet = SendRequest.sendPost(url, "data="+jsonSetUserInfo.toString());		System.out.println("SetUserInfoTest:"+strRet);	}}