package rmi.corpusManger;import java.rmi.Remote;import java.rmi.RemoteException;import java.util.Map;import weibo4j.model.User;import weibo4j.org.json.JSONObject;import weiboApi.WeiboMsg;public interface CorpusMangerRmi extends Remote {		/**	 * 导入语料，并建立索引	 * @param pUid 用户微博ID 	 * @param pJsonString 远程调用接收参数	 * 		  UID，Parameter为实际所用参数	 * @return 返回状态，以及已经建立索引数	 * 	 * @throws RemoteException	 */	public Map<String, Object> appendIndex(JSONObject pJSON) throws RemoteException;		/**	 * 删除索引	 * @param pUid 用户微博ID 	 * @param pJsonString 远程调用接收参数	 * 		  UID，Parameter为实际所用参数	 * @return 返回状态，以及已经建立索引数	 * 	 * @throws RemoteException	 */	public Map<String, Object> deleteIndex(JSONObject pJSON) throws RemoteException;		/**	 * HTTP关键词查询请求	 * @return	 * @throws RemoteException	 */	public Map<String, Object> corpusKeywordsFilter(JSONObject pJSON) throws RemoteException;		/**	 * 删除用户	 * @throws RemoteException	 */	public Map<String, Object> deleteUser(JSONObject pJSON) throws RemoteException;			/**	 * 根据指定目录从新初始化MangerMap	 * @return true		成功	 * 		   false	失败	 * @throws RemoteException	 */	public boolean reboot() throws RemoteException;	}