package rmi.messageCenter;import java.rmi.Remote;import java.rmi.RemoteException;import java.util.Map;import weibo4j.org.json.JSONObject;public interface MessageCenterRmi extends Remote {	/**	 * HTTP 调用该方法回复用户消息	 * 	 * @param pstrUid	 * @param pStrMid	 * @param pstrContent	 * @return	 * @throws RemoteException	 */	public Map<String, Object> replyMessage(JSONObject pJson) throws RemoteException;	/**	 * HTTP关键词查询请求	 * 	 * @return	 * @throws RemoteException	 */	public Map<String, Object> messageKeywordsFilter(JSONObject pJson) throws RemoteException;		/**	 * 增加新用户	 * @param pStrUid	用户UID	 * @param pStrAccessToken	token	 * @param pCorGroupInfo		CorpusManger组别	 * @return	 * @throws RemoteException	 */	public Map<String, Object> addUser(JSONObject pJson) throws RemoteException;			/**	 * 删除用户	 * @param pStrUid	 * @return	 * @throws RemoteException	 */	public Map<String, Object> deleteUser(JSONObject pJson) throws RemoteException;			/**	 * 	 * @param pJson	 * @return	 * @throws RemoteException	 */	public Map<String,Object> setSetting(JSONObject pJson) throws RemoteException;		/**	 * 查看现在正在监听的用户列表	 * @param pStrUid	 * @return	 * @throws RemoteException	 */	public Map<String, Object> userStat() throws RemoteException;}